using UnityEngine;
using UnityEditor;
using System.Collections;
using UnityEditor.Callbacks;
using UnityEditorInternal;


[CustomEditor(typeof(AmplifyMotionEffect))]
public class AmplifyMotionEffectEditor : Editor
{
    private static Texture2D texMotionLogoforInspectors = null;
    private static GUIStyle wrapableText = null;


    public override void OnInspectorGUI()
    {
        int iAlignmentSpace = (Screen.width - 330) / 2;
        Color defaultBgColor = GUI.backgroundColor;
        GUILayout.Space(10);

        CreateLogoMotion(iAlignmentSpace);
        CreateVersionHeader("Amplify Motion 1.6");

        GUILayout.BeginVertical("Box");
        {
            BoxTitle("Feature Limited Version", EditorStyles.boldLabel);
            EditorGUILayout.Space();
            GUILayout.Label("This version of Amplify Motion is feature limited and provided to use with RGK only.\r\n \r\nRetail version has more advanced features and no limitations as in this version.", WrappableLabel);
            EditorGUILayout.Space();

            GUI.backgroundColor = Color.red;
            GUILayout.BeginHorizontal();
            {
                if (GUILayout.Button("Asset Store", (GUILayout.Height(35))))
                {
                    Application.OpenURL("https://www.assetstore.unity3d.com/#!/content/4243");
                }
                if (GUILayout.Button("Amplify Website", (GUILayout.Height(35))))
                {
                    Application.OpenURL("http://amplify.pt/?ref=rgk");
                }
            }
            GUILayout.EndHorizontal();

            GUI.backgroundColor = defaultBgColor;

            EditorGUILayout.Space();
        }
        GUILayout.EndVertical();

    }
    public static void CreateVersionHeader(string InspectorName)
    {
        int defaultIndent = EditorGUI.indentLevel;
        Color defaultColor = GUI.backgroundColor;
        GUI.backgroundColor = Color.red;
        GUILayout.BeginVertical("Box");
        EditorGUI.indentLevel = 0;
        EditorGUILayout.LabelField(InspectorName, EditorStyles.boldLabel);
        GUILayout.EndVertical();
        EditorGUILayout.Space();
        GUI.backgroundColor = defaultColor;
        EditorGUI.indentLevel = defaultIndent;
    }

    private static void CreateLogoMotion(float AlignmentSpace)
    {
        GUILayout.BeginVertical();
        {
            GUILayout.BeginHorizontal();
            {
                GUILayout.Space(AlignmentSpace);
                GUILayout.Box(LogoForInspector, GUIStyle.none, GUILayout.Width(310), GUILayout.Height(60));
            }
            GUILayout.EndHorizontal();

        }
        GUILayout.EndVertical();
        EditorGUILayout.Space();
    }
    private static void BoxTitle(string TitleText, GUIStyle TitleStyle)
    {

        Color defBGColor = GUI.backgroundColor;

        GUI.backgroundColor = Color.red;
        GUILayout.BeginVertical("Box");
        {
            EditorGUILayout.LabelField(TitleText, TitleStyle);
        }
        GUILayout.EndVertical();
        GUI.backgroundColor = defBGColor;
        GUILayout.Space(5);
    }
    public static GUIStyle WrappableLabel
    {
        get
        {
            if (wrapableText == null)
            {
                wrapableText = new GUIStyle(EditorStyles.label);
                wrapableText.normal.textColor = EditorStyles.label.normal.textColor;
                wrapableText.wordWrap = true;
                wrapableText.stretchWidth = true;
            }
            return wrapableText;
        }
    }
    public static Texture2D LogoForInspector
    {
        get
        {
            if (texMotionLogoforInspectors == null)
            {
                texMotionLogoforInspectors = new Texture2D(310, 60, TextureFormat.ARGB32, false);
                texMotionLogoforInspectors.hideFlags = HideFlags.DontSave;
                texMotionLogoforInspectors.anisoLevel = 0;
                if (EditorGUIUtility.isProSkin)
                {
                    texMotionLogoforInspectors.LoadImage(System.Convert.FromBase64String(""));
                }
                else
                {
                    texMotionLogoforInspectors.LoadImage(System.Convert.FromBase64String(""));
                }
            }
            return texMotionLogoforInspectors;
        }
    }
}